<?php//CONTACT FORM 7add_filter( 'wpcf7_form_class_attr', 'wpl_contact_form_7' );function wpl_contact_form_7( $content ) {	$rl_formfind = '/wpcf7-form/';	$rl_formreplace = 'wpcf7-form wpl-track-me contact-form-7-form';	$content = preg_replace( $rl_formfind, $rl_formreplace, $content );	return $content;}//GRAVITY FORMS//ECHO 1; EXIT;add_filter("gform_form_tag", "wpl_gravity_forms", 10, 2);function wpl_gravity_forms($form_tag, $form){	if (stristr($form_tag,'class='))	{		$form_tag = preg_replace("|class='(.*?)'|", "class='$1 wpl-track-me'", $form_tag);	}	else	{		$form_tag = preg_replace("|action='(.*?)'|", "action='$1' class='wpl-track-me' ", $form_tag);	}    return $form_tag;}//NINJA FORMSadd_filter( 'ninja_forms_display_fields_array', 'wpl_ninja_forms', 10, 2 );function wpl_ninja_forms( $field_results, $form_id ){	//print_r($field_results);exit;    // $data will contain all of the field settings that have been saved for this field.    // Let's change the default value of the field if it has an ID of 3    if( $field_id == 3 ){        $data['default_value'] = 'hello world';    }    return $data;}