<?php/* * Integrations with other form plugins *///CONTACT FORM 7add_filter( 'wpcf7_form_class_attr', 'wpl_contact_form_7' );function wpl_contact_form_7( $content ) {	$rl_formfind = '/wpcf7-form/';	$rl_formreplace = 'wpcf7-form wpl-track-me';	$content = preg_replace( $rl_formfind, $rl_formreplace, $content );	return $content;}//GRAVITY FORMSadd_filter("gform_form_tag", "wpl_gravity_forms", 10, 2);function wpl_gravity_forms($form_tag, $form){	if (stristr($form_tag,'class=')) {		$form_tag = preg_replace("|class='(.*?)'|", "class='$1 wpl-track-me'", $form_tag);	} else {		$form_tag = preg_replace("|action='(.*?)'|", "action='$1' class='wpl-track-me' ", $form_tag);	}    return $form_tag;}//NINJA FORMSadd_filter( 'ninja_forms_form_class', 'wpl_ninja_forms', 10, 2 );function wpl_ninja_forms( $form_class, $form_id ){	$form_class = $form_class." wpl-track-me";    return $form_class;}